pragma solidity ^0.4.23;

contract Charity {
    uint public requiredFund;
    address public receiverAddress;
    uint public deadLine; // time for activity (in days)
    uint ETH = 1 ether;
    address public owner;
    uint public totalFund;

    uint public start = now/1 days;

    address[] public senders;
    uint public numberOfSenders = 0;

    constructor(uint _requiredFund, address _receiverAddress, uint _day) public {
        owner = msg.sender;
        requiredFund = _requiredFund; // (in ETH)
        receiverAddress = _receiverAddress;
        deadLine = start + _day;
    }

    function sendMoney(uint _value, bool publicInfo) public payable {
        require(now / 1 days <= deadLine);
        require(msg.sender.balance >= msg.value);
        require(msg.value >= _value*ETH); // value ở đây là số tiền mình sẽ chuyển vào contract (chỗ value ở Remix); hàm đảm bảo số tiền chuyển vào contract phải >= số tiền sẽ chuyển đi
        if (msg.value > _value*ETH) {
            msg.sender.transfer(msg.value - _value*ETH); // hàm rút lại số tiền thừa của phần chuyển vào nếu nó lớn hơn chỗ chuyển đi
        }
        totalFund += _value;

        if(publicInfo == true) {
            senders.push(msg.sender);
            numberOfSenders ++;
        }

        if(totalFund >= requiredFund) {
            sendToReceiver();
        }
    }

    function overTime() public returns (bool) {
